import { useState } from "react";

const Homepage = () =>{

    const[loggedIn, setLoggedIn] =useState(false);

    const logIn = ()=>{
        if(loggedIn === false){
            setLoggedIn = true;
            document.getElementsByClassName('login').style.display="flex";
        }
        else{
            setLoggedIn = false;
            document.getElementsByClassName('login').style.display="none";
        }
        
    }

    return (
        <div className="homepage">
            <form className="login">
                <div className="login-header">
                    <h2>LOGIN FORM</h2>
                    <button className="login-close">X</button>
                </div>
                <input type="text" placeholder="Enter your username"/>
                
                <input type="password" placeholder="Enter your password"/>
                <button className="login-btn" type="submit">LOGIN</button>
                <p>Not registered? <a href="">Create an account</a></p>
            </form>

            <h1>Get up to 30% Off New Arrivals</h1>
            <button className="homepage-button" onClick={logIn}>SHOP NOW</button>
        </div>
    );
};

export default Homepage;
